import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {
  registerForm: FormGroup;
  loading = false;
  submitted = false;
  error = '';
  success = '';

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private authService: AuthService
  ) {
    // Inicializa o formulário vazio
    this.registerForm = this.formBuilder.group({
      username: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(50)]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      confirmPassword: ['', Validators.required]
    }, {
      validator: this.passwordMatchValidator
    });
  }

  ngOnInit(): void {
    // Se o usuário já estiver logado, redireciona para a página inicial
    if (this.authService.isLoggedIn()) {
      this.router.navigate(['/home']);
    }
  }

  // Getter para fácil acesso aos campos do formulário
  get f() { return this.registerForm.controls; }

  // Validador personalizado para verificar se as senhas coincidem
  passwordMatchValidator(formGroup: FormGroup) {
    const password = formGroup.get('password')?.value;
    const confirmPassword = formGroup.get('confirmPassword')?.value;

    if (password !== confirmPassword) {
      formGroup.get('confirmPassword')?.setErrors({ passwordMismatch: true });
      return { passwordMismatch: true };
    } else {
      formGroup.get('confirmPassword')?.setErrors(null);
      return null;
    }
  }

  onSubmit(): void {
    this.submitted = true;
    this.error = '';
    this.success = '';

    // Para se o formulário for inválido
    if (this.registerForm.invalid) {
      return;
    }

    this.loading = true;
    console.log('Enviando requisição de registro...');

    const { username, email, password } = this.registerForm.value;

    this.authService.register(username, email, password)
      .subscribe({
        next: (response) => {
          console.log('Resposta do registro:', response);
          if (response.success) {
            this.success = 'Cadastro realizado com sucesso! Redirecionando para o login...';
            setTimeout(() => {
              this.router.navigate(['/login']);
            }, 2000);
          } else {
            this.error = response.message || 'Erro ao cadastrar usuário';
            this.loading = false;
          }
        },
        error: (error) => {
          console.error('Erro no registro:', error);
          this.error = error.error?.message || 'Ocorreu um erro ao tentar cadastrar. Tente novamente.';
          this.loading = false;
        }
      });
  }
}
<div class="register-container">
  <div class="card">
    <h2 class="card-header">Cadastro de Usuário</h2>
    <div class="card-body">
      <div *ngIf="error" class="alert alert-danger">{{error}}</div>
      <div *ngIf="success" class="alert alert-success">{{success}}</div>

      <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
        <div class="form-group">
          <label for="username">Nome de Usuário</label>
          <input 
            type="text" 
            formControlName="username" 
            class="form-control" 
            [ngClass]="{ 'is-invalid': submitted && f['username'].errors }" 
          />
          <div *ngIf="submitted && f['username'].errors" class="invalid-feedback">
            <div *ngIf="f['username'].errors['required']">Nome de usuário é obrigatório</div>
            <div *ngIf="f['username'].errors['minlength']">Nome de usuário deve ter pelo menos 3 caracteres</div>
            <div *ngIf="f['username'].errors['maxlength']">Nome de usuário não pode ter mais de 50 caracteres</div>
          </div>
        </div>

        <div class="form-group">
          <label for="email">Email</label>
          <input 
            type="email" 
            formControlName="email" 
            class="form-control" 
            [ngClass]="{ 'is-invalid': submitted && f['email'].errors }" 
          />
          <div *ngIf="submitted && f['email'].errors" class="invalid-feedback">
            <div *ngIf="f['email'].errors['required']">Email é obrigatório</div>
            <div *ngIf="f['email'].errors['email']">Email inválido</div>
          </div>
        </div>

        <div class="form-group">
          <label for="password">Senha</label>
          <input 
            type="password" 
            formControlName="password" 
            class="form-control" 
            [ngClass]="{ 'is-invalid': submitted && f['password'].errors }" 
          />
          <div *ngIf="submitted && f['password'].errors" class="invalid-feedback">
            <div *ngIf="f['password'].errors['required']">Senha é obrigatória</div>
            <div *ngIf="f['password'].errors['minlength']">Senha deve ter pelo menos 6 caracteres</div>
          </div>
        </div>

        <div class="form-group">
          <label for="confirmPassword">Confirmar Senha</label>
          <input 
            type="password" 
            formControlName="confirmPassword" 
            class="form-control" 
            [ngClass]="{ 'is-invalid': submitted && f['confirmPassword'].errors }" 
          />
          <div *ngIf="submitted && f['confirmPassword'].errors" class="invalid-feedback">
            <div *ngIf="f['confirmPassword'].errors['required']">Confirmação de senha é obrigatória</div>
            <div *ngIf="f['confirmPassword'].errors['passwordMismatch']">As senhas não coincidem</div>
          </div>
        </div>

        <div class="form-group">
          <button [disabled]="loading" class="btn btn-primary">
            <span *ngIf="loading" class="spinner-border spinner-border-sm mr-1"></span>
            Cadastrar
          </button>
        </div>

        <div class="form-group login-link">
          <p>Já possui uma conta? <a routerLink="/login">Fazer Login</a></p>
        </div>
      </form>
    </div>
  </div>
</div>
