import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { FabricanteService } from '../../services/fabricante.service';
import { Fabricante } from '../../services/produto.service';

@Component({
  selector: 'app-fabricante-list',
  templateUrl: './fabricante-list.component.html',
  styleUrls: ['./fabricante-list.component.css']
})
export class FabricanteListComponent implements OnInit {
  fabricantes: Fabricante[] = [];
  loading = false;
  error = '';
  successMessage = '';

  constructor(
    private fabricanteService: FabricanteService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loadFabricantes();
  }

  loadFabricantes(): void {
    this.loading = true;
    this.error = '';
    this.fabricanteService.getFabricantes().subscribe(response => {
      this.loading = false;
      if (response.success) {
        this.fabricantes = Array.isArray(response.data) ? response.data : [response.data];
      } else {
        this.error = response.message || 'Erro ao carregar fabricantes.';
      }
    }, error => {
      this.loading = false;
      this.error = 'Erro ao carregar fabricantes.';
    });
  }

  editarFabricante(id: string | undefined): void {
    if (!id) return;
    this.router.navigate(['/fabricantes/edit', id]);
  }

  excluirFabricante(id: string | undefined): void {
    if (!id) return;

    if (confirm('Tem certeza que deseja excluir este fabricante?')) {
      this.fabricanteService.deleteFabricante(id).subscribe(response => {
        if (response.success) {
          this.successMessage = 'Fabricante excluído com sucesso!';
          this.fabricantes = this.fabricantes.filter(fabricante => fabricante._id !== id);

          // Esconde a mensagem após 3 segundos
          setTimeout(() => {
            this.successMessage = '';
          }, 3000);
        } else {
          this.error = response.message || 'Erro ao excluir fabricante.';
        }
      }, error => {
        this.error = 'Erro ao excluir fabricante.';
      });
    }
  }

  adicionarFabricante(): void {
    this.router.navigate(['/fabricantes/add']);
  }
}
<div class="container mt-4">
  <div class="d-flex justify-content-between align-items-center mb-4">
    <h2>Gerenciamento de Fabricantes</h2>
    <button class="btn btn-primary" (click)="adicionarFabricante()">
      <i class="bi bi-plus-circle"></i> Novo Fabricante
    </button>
  </div>

  <div *ngIf="successMessage" class="alert alert-success alert-dismissible fade show" role="alert">
    {{ successMessage }}
    <button type="button" class="btn-close" (click)="successMessage = ''" aria-label="Close"></button>
  </div>

  <div *ngIf="error" class="alert alert-danger alert-dismissible fade show" role="alert">
    {{ error }}
    <button type="button" class="btn-close" (click)="error = ''" aria-label="Close"></button>
  </div>

  <div *ngIf="loading" class="text-center my-5">
    <div class="spinner-border text-primary" role="status">
      <span class="visually-hidden">Carregando...</span>
    </div>
  </div>

  <div *ngIf="!loading && fabricantes.length === 0" class="alert alert-info text-center">
    Nenhum fabricante encontrado. Adicione um novo fabricante clicando no botão acima.
  </div>

  <div *ngIf="!loading && fabricantes.length > 0" class="table-responsive">
    <table class="table table-striped table-hover">
      <thead>
        <tr>
          <th>Nome</th>
          <th>CNPJ</th>
          <th>Razão Social</th>
          <th>Telefone</th>
          <th>Contato</th>
          <th>Email</th>
          <th>Ações</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let fabricante of fabricantes">
          <td>{{ fabricante.nome }}</td>
          <td>{{ fabricante.cnpj }}</td>
          <td>{{ fabricante.razao_social }}</td>
          <td>{{ fabricante.telefone }}</td>
          <td>{{ fabricante.contato }}</td>
          <td>{{ fabricante.email }}</td>
          <td>
            <div class="btn-group" role="group">
              <button class="btn btn-sm btn-outline-primary" (click)="editarFabricante(fabricante._id)">
                <i class="bi bi-pencil"></i>
              </button>
              <button class="btn btn-sm btn-outline-danger" (click)="excluirFabricante(fabricante._id)">
                <i class="bi bi-trash"></i>
              </button>
            </div>
          </td>
        </tr>
      </tbody>
    </table>
  </div>

  <div class="mt-4">
    <button class="btn btn-secondary" routerLink="/home">Voltar</button>
  </div>
</div>
