import { Component, OnInit } from '@angular/core';
import { AuthService, User } from '../auth/auth.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  currentUser: User | null = null;

  constructor(private authService: AuthService) { }

  ngOnInit(): void {
    this.authService.currentUser.subscribe(user => {
      this.currentUser = user;
    });
  }

  logout(): void {
    this.authService.logout();
  }
}
<div class="home-container">
  <header class="app-header">
    <h1>Sistema de Gestão de Vendas</h1>
    <div class="user-info">
      <span *ngIf="currentUser">Olá, {{currentUser.username}}</span>
      <button class="logout-btn" (click)="logout()">Sair</button>
    </div>
  </header>

  <div class="content">
    <h2>Bem-vindo ao Sistema de Gestão de Vendas</h2>
    <p>Você está logado como <strong>{{currentUser?.role}}</strong>.</p>
    <p>Agora você pode gerenciar vendas, produtos e clientes.</p>

    <div class="dashboard">
      <div class="card">
        <h3>Vendas</h3>
        <p>Gerencie as vendas do sistema</p>
        <button>Ver Vendas</button>
      </div>

      <div class="card">
        <h3>Produtos</h3>
        <p>Gerencie o catálogo de produtos</p>
        <button>Ver Produtos</button>
      </div>

      <div class="card">
        <h3>Clientes</h3>
        <p>Gerencie os clientes da empresa</p>
        <button routerLink="/clientes">Ver Clientes</button>
      </div>
    </div>
  </div>
</div>
