import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { VendaService, Venda, VendaResponse } from '../../services/venda.service';
import { ClienteService, Cliente } from '../../services/cliente.service';
import { ProdutoService, Produto } from '../../services/produto.service';

@Component({
  selector: 'app-venda-form',
  templateUrl: './venda-form.component.html',
  styleUrls: ['./venda-form.component.css']
})
export class VendaFormComponent implements OnInit {
  vendaForm!: FormGroup;
  isEditMode = false;
  vendaId: string | null = null;
  loading = false;
  saving = false;
  error = '';
  successMessage = '';
  clientes: Cliente[] = [];
  produtos: Produto[] = [];

  constructor(
    private fb: FormBuilder,
    private vendaService: VendaService,
    private clienteService: ClienteService,
    private produtoService: ProdutoService,
    private route: ActivatedRoute,
    private router: Router
  ) {
    this.initForm();
  }

  ngOnInit(): void {
    this.carregarClientes();
    this.carregarProdutos();

    this.route.params.subscribe((params: {id?: string}) => {
      if (params['id']) {
        this.isEditMode = true;
        this.vendaId = params['id'];
        this.carregarVenda(this.vendaId);
      }
    });
  }

  initForm(): void {
    const today = new Date();

    this.vendaForm = this.fb.group({
      identificador: ['', [Validators.required]],
      dia: [today.getDate(), [Validators.required, Validators.min(1), Validators.max(31)]],
      mes: [today.getMonth() + 1, [Validators.required, Validators.min(1), Validators.max(12)]],
      ano: [today.getFullYear(), [Validators.required, Validators.min(2000)]],
      valor_total: ['', [Validators.required, Validators.min(0)]],
      clnt: ['', [Validators.required]],
      prod: ['', [Validators.required]]
    });
  }

  carregarClientes(): void {
    this.clienteService.getClientes().subscribe(
      response => {
        if (response.success && Array.isArray(response.data)) {
          this.clientes = response.data;
        }
      },
      error => {
        console.error('Erro ao carregar clientes', error);
      }
    );
  }

  carregarProdutos(): void {
    this.produtoService.getProdutos().subscribe(
      response => {
        if (response.success && Array.isArray(response.data)) {
          this.produtos = response.data;
        }
      },
      error => {
        console.error('Erro ao carregar produtos', error);
      }
    );
  }

  carregarVenda(id: string | null): void {
    if (!id) return;
    this.loading = true;
    this.vendaService.getVenda(id).subscribe(
      (response: VendaResponse) => {
        this.loading = false;
        if (response.success && response.data) {
          const venda = response.data as Venda;
          this.vendaForm.patchValue({
            identificador: venda.identificador,
            dia: venda.dia,
            mes: venda.mes,
            ano: venda.ano,
            valor_total: venda.valor_total,
            clnt: venda.clnt,
            prod: venda.prod
          });
        } else {
          this.error = response.message || 'Erro ao carregar venda.';
        }
      },
      (error) => {
        this.loading = false;
        this.error = 'Erro ao carregar venda.';
        console.error(error);
      }
    );
  }

  onSubmit(): void {
    if (this.vendaForm.invalid) {
      this.markFormGroupTouched(this.vendaForm);
      return;
    }

    this.saving = true;
    const vendaData: Venda = this.vendaForm.value;

    if (this.isEditMode && this.vendaId) {
      this.vendaService.updateVenda(this.vendaId, vendaData).subscribe(
        (response: VendaResponse) => {
          this.saving = false;
          if (response.success) {
            this.successMessage = 'Venda atualizada com sucesso!';
            setTimeout(() => {
              this.router.navigate(['/vendas']);
            }, 2000);
          } else {
            this.error = response.message || 'Erro ao atualizar venda.';
          }
        },
        (error) => {
          this.saving = false;
          this.error = 'Erro ao atualizar venda.';
          console.error(error);
        }
      );
    } else {
      this.vendaService.createVenda(vendaData).subscribe(
        (response: VendaResponse) => {
          this.saving = false;
          if (response.success) {
            this.successMessage = 'Venda cadastrada com sucesso!';
            this.vendaForm.reset();
            setTimeout(() => {
              this.router.navigate(['/vendas']);
            }, 2000);
          } else {
            this.error = response.message || 'Erro ao cadastrar venda.';
          }
        },
        (error) => {
          this.saving = false;
          this.error = 'Erro ao cadastrar venda.';
          console.error(error);
        }
      );
    }
  }

  markFormGroupTouched(formGroup: FormGroup): void {
    Object.values(formGroup.controls).forEach(control => {
      control.markAsTouched();
      if (control instanceof FormGroup) {
        this.markFormGroupTouched(control);
      }
    });
  }

  getFieldError(controlName: string): string {
    const control = this.vendaForm.get(controlName);
    if (control?.touched && control?.errors) {
      if (control.errors['required']) return 'Este campo é obrigatório.';
      if (control.errors['min']) return `Valor mínimo: ${control.errors['min'].min}.`;
      if (control.errors['max']) return `Valor máximo: ${control.errors['max'].max}.`;
    }
    return '';
  }

  getNomeProduto(id: string): string {
    const produto = this.produtos.find(p => p._id === id);
    return produto ? produto.nome : 'Produto não encontrado';
  }

  getNomeCliente(id: string): string {
    const cliente = this.clientes.find(c => c._id === id);
    return cliente ? cliente.nome : 'Cliente não encontrado';
  }
}
<div class="container">
  <div class="header">
    <h2>{{ isEditMode ? 'Editar Venda' : 'Nova Venda' }}</h2>
  </div>

  <div *ngIf="error" class="error-message">
    {{ error }}
  </div>

  <div *ngIf="successMessage" class="success-message">
    {{ successMessage }}
  </div>

  <div *ngIf="loading" class="loading">
    <div class="spinner"></div>
    <p>Carregando dados...</p>
  </div>

  <form [formGroup]="vendaForm" (ngSubmit)="onSubmit()" *ngIf="!loading">
    <div class="form-row">
      <div class="form-group">
        <label for="identificador">Identificador*</label>
        <input type="text" id="identificador" formControlName="identificador" 
               [class.is-invalid]="vendaForm.get('identificador')?.invalid && vendaForm.get('identificador')?.touched">
        <div class="error-text" *ngIf="getFieldError('identificador')">
          {{ getFieldError('identificador') }}
        </div>
      </div>
    </div>

    <div class="form-row">
      <div class="form-group">
        <label for="dia">Dia*</label>
        <input type="number" id="dia" formControlName="dia" min="1" max="31"
               [class.is-invalid]="vendaForm.get('dia')?.invalid && vendaForm.get('dia')?.touched">
        <div class="error-text" *ngIf="getFieldError('dia')">
          {{ getFieldError('dia') }}
        </div>
      </div>

      <div class="form-group">
        <label for="mes">Mês*</label>
        <input type="number" id="mes" formControlName="mes" min="1" max="12"
               [class.is-invalid]="vendaForm.get('mes')?.invalid && vendaForm.get('mes')?.touched">
        <div class="error-text" *ngIf="getFieldError('mes')">
          {{ getFieldError('mes') }}
        </div>
      </div>

      <div class="form-group">
        <label for="ano">Ano*</label>
        <input type="number" id="ano" formControlName="ano" min="2000"
               [class.is-invalid]="vendaForm.get('ano')?.invalid && vendaForm.get('ano')?.touched">
        <div class="error-text" *ngIf="getFieldError('ano')">
          {{ getFieldError('ano') }}
        </div>
      </div>
    </div>

    <div class="form-row">
      <div class="form-group">
        <label for="clnt">Cliente*</label>
        <select id="clnt" formControlName="clnt" 
                [class.is-invalid]="vendaForm.get('clnt')?.invalid && vendaForm.get('clnt')?.touched">
          <option value="">Selecione um cliente</option>
          <option *ngFor="let cliente of clientes" [value]="cliente._id">{{ cliente.nome }}</option>
        </select>
        <div class="error-text" *ngIf="getFieldError('clnt')">
          {{ getFieldError('clnt') }}
        </div>
      </div>

      <div class="form-group">
        <label for="prod">Produto*</label>
        <select id="prod" formControlName="prod" 
                [class.is-invalid]="vendaForm.get('prod')?.invalid && vendaForm.get('prod')?.touched">
          <option value="">Selecione um produto</option>
          <option *ngFor="let produto of produtos" [value]="produto._id">{{ produto.nome }}</option>
        </select>
        <div class="error-text" *ngIf="getFieldError('prod')">
          {{ getFieldError('prod') }}
        </div>
      </div>
    </div>

    <div class="form-row">
      <div class="form-group">
        <label for="valor_total">Valor Total*</label>
        <input type="number" id="valor_total" formControlName="valor_total" min="0" step="0.01"
               [class.is-invalid]="vendaForm.get('valor_total')?.invalid && vendaForm.get('valor_total')?.touched">
        <div class="error-text" *ngIf="getFieldError('valor_total')">
          {{ getFieldError('valor_total') }}
        </div>
      </div>
    </div>

    <div class="form-actions">
      <button type="submit" class="btn-primary" [disabled]="saving">
        {{ saving ? 'Salvando...' : (isEditMode ? 'Atualizar' : 'Cadastrar') }}
      </button>
      <button type="button" class="btn-secondary" routerLink="/vendas">Cancelar</button>
    </div>
  </form>

  <div class="back-link">
    <a routerLink="/vendas">Voltar para a lista de vendas</a>
  </div>
</div>
