import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router, ActivatedRoute } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  loginForm: FormGroup;
  loading = false;
  submitted = false;
  returnUrl: string = '/';
  error: string = '';
  success: string = '';

  constructor(
    private formBuilder: FormBuilder,
    private route: ActivatedRoute,
    private router: Router,
    private authService: AuthService
  ) {
    // Redirect to home if already logged in
    if (this.authService.isLoggedIn()) {
      this.router.navigate(['/']);
    }

    this.loginForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });
  }

  ngOnInit(): void {
    // Get return url from route parameters or default to '/'
    this.returnUrl = this.route.snapshot.queryParams['returnUrl'] || '/';

    // Show success message if registration was successful
    if (this.route.snapshot.queryParams['registered']) {
      this.success = 'Registro realizado com sucesso! Faça login para continuar.';
    }
  }

  // Convenience getter for easy access to form fields
  get f() { return this.loginForm.controls; }

  onSubmit(): void {
    this.submitted = true;
    this.error = '';
    this.success = '';

    // Stop here if form is invalid
    if (this.loginForm.invalid) {
      return;
    }

    this.loading = true;
    console.log('Enviando requisição de login...');
    this.authService.login(this.f['email'].value, this.f['password'].value)
      .subscribe({
        next: (response) => {
          console.log('Resposta do login:', response);
          if (response.success) {
            // Set a success message
            this.success = 'Login realizado com sucesso! Redirecionando...';

            // Small delay before navigation to show the success message
            setTimeout(() => {
              this.router.navigateByUrl(this.returnUrl);
            }, 1000);
          } else {
            this.error = response.message || 'Erro ao fazer login';
            this.loading = false;
          }
        },
        error: (error) => {
          console.error('Erro no login:', error);
          this.error = 'Ocorreu um erro ao tentar fazer login. Tente novamente.';
          this.loading = false;
        }
      });
  }
}
<div class="login-container">
  <div class="login-card">
    <div class="login-header">
      <h1>Sistema de Gestão de Vendas</h1>
      <h2>Login</h2>
    </div>

    <div *ngIf="error" class="alert alert-danger">
      {{ error }}
    </div>

    <div *ngIf="success" class="alert alert-success">
      {{ success }}
    </div>

    <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="email">Email</label>
        <input
          type="email"
          formControlName="email"
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f['email'].errors }"
          placeholder="Digite seu email"
        />
        <div *ngIf="submitted && f['email'].errors" class="invalid-feedback">
          <div *ngIf="f['email'].errors['required']">Email é obrigatório</div>
          <div *ngIf="f['email'].errors['email']">Email deve ser um endereço válido</div>
        </div>
      </div>

      <div class="form-group">
        <label for="password">Senha</label>
        <input
          type="password"
          formControlName="password"
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f['password'].errors }"
          placeholder="Digite sua senha"
        />
        <div *ngIf="submitted && f['password'].errors" class="invalid-feedback">
          <div *ngIf="f['password'].errors['required']">Senha é obrigatória</div>
          <div *ngIf="f['password'].errors['minlength']">Senha deve ter pelo menos 6 caracteres</div>
        </div>
      </div>

      <div class="form-group">
        <button [disabled]="loading" class="btn btn-primary btn-block">
          <span *ngIf="loading" class="spinner-border spinner-border-sm mr-1"></span>
          Entrar
        </button>
      </div>
    </form>
  </div>
</div>
