import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ProdutoService, Produto } from '../../services/produto.service';

@Component({
  selector: 'app-produto-list',
  templateUrl: './produto-list.component.html',
  styleUrls: ['./produto-list.component.css']
})
export class ProdutoListComponent implements OnInit {
  produtos: Produto[] = [];
  loading = false;
  error = '';
  successMessage = '';

  constructor(
    private produtoService: ProdutoService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loadProdutos();
  }

  loadProdutos(): void {
    this.loading = true;
    this.error = '';
    this.produtoService.getProdutos().subscribe(response => {
      this.loading = false;
      if (response.success) {
        this.produtos = Array.isArray(response.data) ? response.data : [response.data];
      } else {
        this.error = response.message || 'Erro ao carregar produtos';
      }
    });
  }

  editarProduto(id: string): void {
    this.router.navigate(['/produtos/editar', id]);
  }

  criarProduto(): void {
    this.router.navigate(['/produtos/novo']);
  }

  excluirProduto(id: string): void {
    if (confirm('Tem certeza que deseja excluir este produto?')) {
      this.produtoService.deleteProduto(id).subscribe(response => {
        if (response.success) {
          this.successMessage = 'Produto excluído com sucesso!';
          this.produtos = this.produtos.filter(produto => produto._id !== id);
          setTimeout(() => {
            this.successMessage = '';
          }, 3000);
        } else {
          this.error = response.message || 'Erro ao excluir produto';
          setTimeout(() => {
            this.error = '';
          }, 3000);
        }
      });
    }
  }

  formatarValor(valor: number): string {
    return valor.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' });
  }

  getFabricanteNome(fabricante: any): string {
    return fabricante && typeof fabricante !== 'string' ? fabricante.nome : 'Não disponível';
  }
}
<div class="container">
  <div class="header">
    <h2>Gerenciamento de Produtos</h2>
    <button class="btn-primary" (click)="criarProduto()">Novo Produto</button>
  </div>

  <div *ngIf="loading" class="loading">
    <div class="spinner"></div>
    <p>Carregando produtos...</p>
  </div>

  <div *ngIf="error" class="error-message">
    {{ error }}
  </div>

  <div *ngIf="successMessage" class="success-message">
    {{ successMessage }}
  </div>

  <div *ngIf="!loading && !error && produtos.length === 0" class="empty-state">
    <p>Nenhum produto encontrado. Clique em "Novo Produto" para adicionar.</p>
  </div>

  <div *ngIf="!loading && produtos.length > 0" class="table-container">
    <table class="data-table">
      <thead>
        <tr>
          <th>Nome</th>
          <th>Categoria</th>
          <th>Preço de Custo</th>
          <th>Preço de Venda</th>
          <th>Fabricante</th>
          <th>Ações</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let produto of produtos">
          <td>{{ produto.nome }}</td>
          <td>{{ produto.categoria }}</td>
          <td>{{ formatarValor(produto.valor_custo) }}</td>
          <td>{{ formatarValor(produto.valor_venda) }}</td>
          <td>{{ getFabricanteNome(produto.fabr) }}</td>
          <td class="actions">
            <button class="btn-edit" (click)="editarProduto(produto._id)">Editar</button>
            <button class="btn-delete" (click)="excluirProduto(produto._id)">Excluir</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>

  <div class="back-link">
    <a routerLink="/home">Voltar para a página inicial</a>
  </div>
</div>
