import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ClienteService, Cliente } from '../../services/cliente.service';

@Component({
  selector: 'app-cliente-list',
  templateUrl: './cliente-list.component.html',
  styleUrls: ['./cliente-list.component.css']
})
export class ClienteListComponent implements OnInit {
  clientes: Cliente[] = [];
  loading = false;
  error = '';
  successMessage = '';

  constructor(
    private clienteService: ClienteService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.carregarClientes();
  }

  carregarClientes(): void {
    this.loading = true;
    this.clienteService.getClientes().subscribe(response => {
      this.loading = false;
      if (response.success) {
        this.clientes = response.data as Cliente[];
      } else {
        this.error = response.message || 'Erro ao carregar clientes.';
      }
    });
  }

  editarCliente(id: string): void {
    this.router.navigate(['/clientes/editar', id]);
  }

  novoCliente(): void {
    this.router.navigate(['/clientes/novo']);
  }

  excluirCliente(cliente: Cliente): void {
    if (confirm(`Deseja realmente excluir o cliente ${cliente.nome}?`)) {
      this.clienteService.deleteCliente(cliente._id!).subscribe(response => {
        if (response.success) {
          this.successMessage = 'Cliente excluído com sucesso!';
          this.clientes = this.clientes.filter(c => c._id !== cliente._id);

          // Limpar mensagem de sucesso após 3 segundos
          setTimeout(() => {
            this.successMessage = '';
          }, 3000);
        } else {
          this.error = response.message || 'Erro ao excluir cliente.';

          // Limpar mensagem de erro após 3 segundos
          setTimeout(() => {
            this.error = '';
          }, 3000);
        }
      });
    }
  }
}
<div class="clientes-container">
  <header class="page-header">
    <h1>Gerenciamento de Clientes</h1>
    <button class="btn-primary novo-btn" (click)="novoCliente()">Novo Cliente</button>
  </header>

  <div *ngIf="loading" class="loading">
    <p>Carregando clientes...</p>
  </div>

  <div *ngIf="error" class="error-message">
    {{ error }}
  </div>

  <div *ngIf="successMessage" class="success-message">
    {{ successMessage }}
  </div>

  <div class="clientes-list" *ngIf="!loading && clientes.length > 0">
    <table>
      <thead>
        <tr>
          <th>Nome</th>
          <th>E-mail</th>
          <th>Telefone</th>
          <th>CPF</th>
          <th>Forma de Pagamento</th>
          <th>Ações</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let cliente of clientes">
          <td>{{ cliente.nome }}</td>
          <td>{{ cliente.email }}</td>
          <td>{{ cliente.telefone }}</td>
          <td>{{ cliente.cpf }}</td>
          <td>{{ cliente.forma_pagamento }}</td>
          <td class="acoes-btns">
            <button class="btn-edit" (click)="editarCliente(cliente._id!)">Editar</button>
            <button class="btn-delete" (click)="excluirCliente(cliente)">Excluir</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>

  <div class="no-data" *ngIf="!loading && clientes.length === 0">
    <p>Nenhum cliente cadastrado. Clique em "Novo Cliente" para adicionar.</p>
  </div>

  <div class="back-button">
    <a routerLink="/home">Voltar para o Início</a>
  </div>
</div>
