import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { FabricanteService } from '../../services/fabricante.service';
import { Fabricante } from '../../services/produto.service';

@Component({
  selector: 'app-fabricante-list',
  templateUrl: './fabricante-list.component.html',
  styleUrls: ['./fabricante-list.component.css']
})
export class FabricanteListComponent implements OnInit {
  fabricantes: Fabricante[] = [];
  loading = false;
  error = '';
  successMessage = '';

  constructor(
    private fabricanteService: FabricanteService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loadFabricantes();
  }

  loadFabricantes(): void {
    this.loading = true;
    this.error = '';
    this.fabricanteService.getFabricantes().subscribe(response => {
      this.loading = false;
      if (response.success) {
        this.fabricantes = Array.isArray(response.data) ? response.data : [response.data];
      } else {
        this.error = response.message || 'Erro ao carregar fabricantes.';
      }
    }, error => {
      this.loading = false;
      this.error = 'Erro ao carregar fabricantes.';
    });
  }

  editarFabricante(id: string | undefined): void {
    if (!id) return;
    this.router.navigate(['/fabricantes/edit', id]);
  }

  excluirFabricante(id: string | undefined): void {
    if (!id) return;

    if (confirm('Tem certeza que deseja excluir este fabricante?')) {
      this.fabricanteService.deleteFabricante(id).subscribe(response => {
        if (response.success) {
          this.successMessage = 'Fabricante excluído com sucesso!';
          this.fabricantes = this.fabricantes.filter(fabricante => fabricante._id !== id);

          // Esconde a mensagem após 3 segundos
          setTimeout(() => {
            this.successMessage = '';
          }, 3000);
        } else {
          this.error = response.message || 'Erro ao excluir fabricante.';
        }
      }, error => {
        this.error = 'Erro ao excluir fabricante.';
      });
    }
  }

  adicionarFabricante(): void {
    this.router.navigate(['/fabricantes/add']);
  }
}
<div class="container">
  <div class="header">
    <h2>Gerenciamento de Fabricantes</h2>
    <button class="btn-primary" (click)="adicionarFabricante()">Novo Fabricante</button>
  </div>

  <div *ngIf="loading" class="loading">
    <div class="spinner"></div>
    <p>Carregando fabricantes...</p>
  </div>

  <div *ngIf="error" class="error-message">
    {{ error }}
  </div>

  <div *ngIf="successMessage" class="success-message">
    {{ successMessage }}
  </div>

  <div *ngIf="!loading && !error && fabricantes.length === 0" class="empty-state">
    <p>Nenhum fabricante encontrado. Clique em "Novo Fabricante" para adicionar.</p>
  </div>

  <div *ngIf="!loading && fabricantes.length > 0" class="table-container">
    <table class="data-table">
      <thead>
        <tr>
          <th>Nome</th>
          <th>CNPJ</th>
          <th>Razão Social</th>
          <th>Telefone</th>
          <th>Contato</th>
          <th>Email</th>
          <th>Ações</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let fabricante of fabricantes">
          <td>{{ fabricante.nome }}</td>
          <td>{{ fabricante.cnpj }}</td>
          <td>{{ fabricante.razao_social }}</td>
          <td>{{ fabricante.telefone }}</td>
          <td>{{ fabricante.contato }}</td>
          <td>{{ fabricante.email }}</td>
          <td class="actions">
            <button class="btn-edit" (click)="editarFabricante(fabricante._id)">Editar</button>
            <button class="btn-delete" (click)="excluirFabricante(fabricante._id)">Excluir</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>

  <div class="back-link">
    <a routerLink="/home">Voltar para a página inicial</a>
  </div>
</div>
